var mongoose = require('mongoose');
var appVersion = mongoose.model('appVersion');

module.exports = function(req, res){
	console.log(req.body);

	var newVersion = new appVersion({
			nameVersion : req.body.nameVersion,
			stringVersion : req.body.numVersion,
			dateRelease : Date(),
			devVersion : req.body.devVersion
		});

		newVersion.save(function(err, newVersionRes) {
			if(err) return res.status(500).send( err.message);
			res.status(200).jsonp(newVersionRes);
		});
}
