var mongoose = require('mongoose');
var appVersion = mongoose.model('appVersion');

module.exports = function(req, res){

	appVersion.findById(req.params.id, function(err, resToUpdate) {

		resToUpdate.nameVersion = req.body.nameVersion;
		resToUpdate.stringVersion = req.body.numVersion;
		resToUpdate.dateRelease = Date();
		resToUpdate.devVersion = req.body.devVersion;

		resToUpdate.save(function(err) {
		if(err) return res.status(500).send(err.message);
			res.status(200).jsonp(resToUpdate);
		});
	});
}
