var colors = require('colors');
var mongoose = require('mongoose');
var companys = mongoose.model('companys');

module.exports = function(req, res){
	

	var newCompany = new companys({
			user : req.body.user,
			nameCompany : req.body.name
		});

		newCompany.save(function(err, resToPost) {
			if(err){
				colors.red(err.message);
				return res.status(200).jsonp({
					error : 500,
					name : err.name,
					message : err.errors
				});
			}
			console.log(colors.inverse.green('>> Nueva Company '+resToPost._id+' <<'));
			res.status(200).jsonp(resToPost);
		});


}
