var colors = require('colors');
var mongoose = require('mongoose');
var crm = mongoose.model('crm');

module.exports = function(req, res){
	

	var newCrm = new crm({
			queue : req.body.queue,
			name : req.body.name,
			message : req.body.massage,
			active : req.body.active
		});

		newCrm.save(function(err, resToPost) {
			if(err){
				colors.red(err.message);
				return res.status(200).jsonp({
					error : 500,
					name : err.name,
					message : err.errors
				});
			}
			console.log(colors.inverse.green('>> Nuevo CRM '+resToPost._id+' <<'));
			res.status(200).jsonp(resToPost);
		});


}
