var mongoose = require('mongoose');
var queues = mongoose.model('queues');

module.exports = function(req, res){

	queues.findById(req.params.id, function(err, resToUpdate) {
		if(resToUpdate == null)
			return res.status(200).jsonp({error:204,message : 'no found result'});

			resToUpdate.nameCompany = req.body.name;

			resToUpdate.icon = req.body.icon;
			resToUpdate.name = req.body.name;
			resToUpdate.caption = req.body.caption;

		resToUpdate.save(function(err) {
		if(err) return res.status(500).send(err.message);
			res.status(200).jsonp(resToUpdate);
		});
	});
}
